mapper.select:
SELECT b.id,e.product_name as skuName,b.number,d.nick_name as nickName,b.create_time as createTime FROM sport_m_product_sku a LEFT JOIN sport_m_product_spu e on a.product_id = e.id LEFT JOIN sport_m_order_info b on a.id = b.product_sku_id LEFT JOIN sport_m_order c on b.order_id = c.id LEFT JOIN sport_user d on c.user_id = d.id WHERE a.stat =1 and b.stat = 1 and c.stat = 1 and e.stat = 1 and b.order_status = 1
and a.product_id = #{productId}
ORDER BY a.create_time desc limit #{page.startNum}, #{page.pageSize}

SELECT b.id,b.star,b.evaluate_status as evaluateStatus,b.user_id as userId,b.evaluate_content as evaluateContent,c.nick_name as nickName,b.pic, c.pic as userPic,b.create_time as createTime,b.last_update_time as lastUpdateTime,b.stat FROM sport_m_product_sku a LEFT JOIN sport_m_evaluate b on a.id = b.product_sku_id LEFT JOIN sport_user c on b.user_id = c.id WHERE a.stat =1 and b.stat = 1 and c.stat = 1
and a.product_id = #{sportMEvaluateDto.productSkuId}
and b.evaluate_status = #{sportMEvaluateDto.evaluateStatus}
and b.pic is not null
ORDER BY a.create_time desc limit #{page.startNum}, #{page.pageSize}

SELECT COUNT(*) FROM sport_m_product_sku a LEFT JOIN sport_m_evaluate b on a.id = b.product_sku_id LEFT JOIN sport_user c on b.user_id = c.id WHERE a.stat =1 and b.stat = 1 and c.stat = 1
and a.product_id = #{sportMEvaluateDto.productSkuId}
and b.evaluate_status = #{sportMEvaluateDto.evaluateStatus}
and b.pic is not null

SELECT a.id,a.product_sku_id as productSkuId,a.spec_id as specId,a.create_time as createTime,a.last_update_time as lastUpdateTime,a.stat
FROM sport_m_product_spec a
WHERE a.stat = 1 and a.product_sku_id = #{productSkuId}

SELECT a.id,a.product_sku_id as productSkuId,a.spec_values_id as specValuesId,a.create_time as createTime,a.last_update_time as lastUpdateTime,a.stat 
FROM sport_m_product_spec_values a 
WHERE a.stat = 1 and a.product_sku_id = #{productSkuId}
